import { Routes, Route, Outlet } from "react-router-dom";
import {
  BlogDetails,
  CategoriesPage,
  Home,
  LoginPage,
  SignupPage,
  WriterPage,
} from "./pages";

function Layout() {
  return (
    <div>
      {/* Header */}
      <header>
        {/* Your header content */}
      </header>

      {/* Main content */}
      <main>
        <Outlet />
      </main>

      {/* Footer */}
      <footer>
        {/* Your footer content */}
      </footer>
    </div>
  );
}

function App() {
  const theme = "light";
  return (
    <main className={theme}>
      <div className={`w-full min-h-screen relative bg-white dark:bg-[#020618]`}>
        <Routes>
          <Route element={<Layout />} />

          <Route path='/' element={<Home />} />
          <Route path='/category' element={<CategoriesPage />} />
          <Route path='/:slug/:id?' element={<BlogDetails />} />
          <Route path='/writer/:id' element={<WriterPage />} />

          <Route path='/sign-up' element={<SignupPage />} />
          <Route path='/sign-in' element={<LoginPage />} />
        </Routes>
      </div>
    </main>
  );
}

export default App;
