import { useGoogleLogin } from "@react-oauth/google";
import React, { useState } from "react";
import { FcGoogle } from "react-icons/fc";
import { Toaster, toast } from "sonner";
import { Link } from "react-router-dom";
import { Button, Divider, Inputbox, Logo } from "../components";

const LoginPage = () => {
  const [data, setData] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setData({
      ...data,
      [name]: value,
    });
  };

  const googleLogin = async () => {
    // Implement Google login logic here
  };

  const handleSubmit = async () => {
    // Implement form submission logic here
  };

  return (
    <div className='flex w-full h-[100vh]'>
      <div className='hidden md:flex flex-col gap-y-4 w-1/3 min-h-screen bg-black items-center justify-between'>
        <Logo type='login' />
        {/* Additional content */}
      </div>
      <div className='flex flex-col justify-center items-center w-full md:w-2/3 bg-white'>
        <Toaster position='top-right' />
        <div className='w-full max-w-sm p-6'>
          <h1 className='text-3xl font-semibold mb-4'>Login</h1>
          <Inputbox
            type='email'
            name='email'
            placeholder='Email'
            value={data.email}
            onChange={handleChange}
          />
          <Inputbox
            type='password'
            name='password'
            placeholder='Password'
            value={data.password}
            onChange={handleChange}
          />
          <Button onClick={handleSubmit}>Login</Button>
          <Divider text='or' />
          <Button
            className='bg-red-600 hover:bg-red-700'
            onClick={googleLogin}
          >
            <FcGoogle className='mr-2' />
            Login with Google
          </Button>
          <div className='mt-4'>
            <span>Don't have an account? </span>
            <Link to='/signup' className='text-blue-600 hover:underline'>
              Sign Up
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
